import React from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image, Alert } from 'react-native';
import { MenuItem } from '../types';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { images } from '../assest/images';

const MENU_ITEMS: MenuItem[] = [
  { id: '1', name: 'Burger', description: 'Juicy beef burger', price: 129.99, image: images.burger, category: 'Main' },
  { id: '2', name: 'Beef Wrap', description: 'Margherita pizza', price: 159.99, image: images.pizza, category: 'Main' },
  { id: '3', name: 'Salad & chicken', description: 'Fresh garden salad', price: 89.99, image: images.salad, category: 'Appetizer' },
  { id: '4', name: 'Pasta', description: 'Creamy alfredo pasta', price: 139.99, image: images.pasta, category: 'Main' },
  { id: '5', name: 'sushi', description: 'Spicy chicken tacos', price: 109.99, image: images.tacos, category: 'Main' },
  { id: '6', name: 'waff', description: 'Spicy buffalo wings', price: 119.99, image: images.wings, category: 'Appetizer' },
  { id: '7', name: 'Fish & Chips', description: 'Crispy fish with fries', price: 149.99, image: images.fish, category: 'Main' },
  { id: '8', name: 'Sandwich', description: 'Club sandwich', price: 99.99, image: images.sandwich, category: 'Main' },
  { id: '9', name: 'Soup', description: 'Tomato basil soup', price: 79.99, image: images.soup, category: 'Appetizer' },
  { id: '10', name: 'Steak', description: 'Grilled ribeye steak', price: 249.99, image: images.steak, category: 'Main' },
  { id: '11', name: 'Dessert', description: 'Chocolate cake', price: 69.99, image: images.dessert, category: 'Dessert' },
  { id: '12', name: 'Fresh Juice', description: 'Orange juice', price: 39.99, image: images.drink, category: 'Beverage' },
  { id: '13', name: 'Fried Rice', description: 'Chicken fried rice', price: 119.99, image: images.rice, category: 'Main' },
  { id: '14', name: 'Grilled Chicken', description: 'Herb grilled chicken', price: 179.99, image: images.chicken, category: 'Main' },
  { id: '15', name: 'Seafood Platter', description: 'Mixed seafood', price: 299.99, image: images.seafood, category: 'Main' }
];

export const MenuScreen: React.FC<{ navigation: any }> = ({ navigation }) => {
  const { addToCart } = useCart();
  const { user } = useAuth();

  const handleAddToCart = (item: MenuItem) => {
    try {
      addToCart(item, user);
    } catch (error) {
      Alert.alert('Registration Required', 'Please register to add items to cart');
    }
  };

  const renderMenuItem = ({ item }: { item: MenuItem }) => (
    <View style={styles.itemContainer}>
      <TouchableOpacity onPress={() => navigation.navigate('MenuItemDetail', { item })}>
        <Image source={item.image} style={styles.image} />
        <Text style={styles.itemName}>{item.name}</Text>
        <Text style={styles.itemDescription}>{item.description}</Text>
        <Text style={styles.itemPrice}>R{item.price}</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.addButton} onPress={() => handleAddToCart(item)}>
        <Text style={styles.addButtonText}>Add to Cart</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={MENU_ITEMS}
        renderItem={renderMenuItem}
        keyExtractor={item => item.id}
        numColumns={2}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, padding: 10 },
  itemContainer: { flex: 1, margin: 5, padding: 10, backgroundColor: 'white', borderRadius: 8, elevation: 2 },
  image: { width: '100%', height: 150, borderRadius: 8, resizeMode: 'cover' },
  itemName: { fontSize: 16, fontWeight: 'bold', marginTop: 5 },
  itemDescription: { fontSize: 12, color: '#666', marginTop: 2 },
  itemPrice: { fontSize: 14, fontWeight: 'bold', color: '#007bff', marginTop: 5 },
  addButton: { backgroundColor: '#007bff', padding: 8, borderRadius: 5, marginTop: 10 },
  addButtonText: { color: 'white', textAlign: 'center', fontSize: 12 }
});
