import React from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Alert } from 'react-native';
import { useCart } from '../context/CartContext';

export const CheckoutScreen: React.FC<{ navigation: any }> = ({ navigation }) => {
  const { cartItems, getCartTotal, clearCart } = useCart();

  const handlePlaceOrder = () => {
    Alert.alert('Order Placed', 'Your order has been placed successfully!', [
      { text: 'OK', onPress: () => {
        clearCart();
        navigation.navigate('Menu');
      }}
    ]);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Order Summary</Text>

      {cartItems.map(item => (
        <View key={item.id} style={styles.orderItem}>
          <Text style={styles.itemName}>{item.menuItem.name}</Text>
          <Text style={styles.itemDetails}>
            ${item.menuItem.price} x {item.quantity} = ${(item.menuItem.price * item.quantity).toFixed(2)}
          </Text>
        </View>
      ))}

      <View style={styles.totalContainer}>
        <Text style={styles.total}>Total: ${getCartTotal().toFixed(2)}</Text>
      </View>

      <TouchableOpacity style={styles.placeOrderButton} onPress={handlePlaceOrder}>
        <Text style={styles.placeOrderButtonText}>Place Order</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, padding: 20, backgroundColor: 'white' },
  title: { fontSize: 24, fontWeight: 'bold', textAlign: 'center', marginBottom: 30 },
  orderItem: { flexDirection: 'row', justifyContent: 'space-between', paddingVertical: 10, borderBottomWidth: 1, borderBottomColor: '#eee' },
  itemName: { fontSize: 16, fontWeight: 'bold' },
  itemDetails: { fontSize: 14, color: '#666' },
  totalContainer: { marginTop: 20, paddingTop: 20, borderTopWidth: 2, borderTopColor: '#007bff' },
  total: { fontSize: 20, fontWeight: 'bold', textAlign: 'center' },
  placeOrderButton: { backgroundColor: '#28a745', padding: 15, borderRadius: 8, marginTop: 30 },
  placeOrderButtonText: { color: 'white', textAlign: 'center', fontSize: 16, fontWeight: 'bold' }
});
