import React, { useState } from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image, Alert, ScrollView } from 'react-native';
import { MenuItem } from '../types';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { images } from '../assest/images';

const MENU_ITEMS: MenuItem[] = [
  { id: '1', name: 'Burger', description: 'Juicy beef burger', price: 85.00, image: images.burger, category: 'Burgers' },
  { id: '2', name: 'Beef Wrap', description: 'Beef wrap with vegetables', price: 75.00, image: images.pizza, category: 'Mains' },
  { id: '3', name: 'Salad & Chicken', description: 'Fresh garden salad with chicken', price: 65.00, image: images.salad, category: 'Starters' },
  { id: '4', name: 'Pasta', description: 'Creamy alfredo pasta', price: 70.00, image: images.pasta, category: 'Mains' },
  { id: '5', name: 'Sushi', description: 'Fresh sushi rolls', price: 95.00, image: images.tacos, category: 'Mains' },
  { id: '6', name: 'Waffle', description: 'Sweet waffle with toppings', price: 45.00, image: images.wings, category: 'Desserts' },
  { id: '7', name: 'Oreo Roller', description: 'Oreo ice cream roll', price: 35.00, image: images.sandwich, category: 'Desserts' },
  { id: '8', name: 'Honey Tarts', description: 'Sweet honey tarts', price: 25.00, image: images.soup, category: 'Desserts' },
  { id: '9', name: 'Chocolate Milkshake', description: 'Chocolate milkshake', price: 40.00, image: images.steak, category: 'Beverages' },
  { id: '10', name: 'Raspberry Cocktail', description: 'Fresh raspberry cocktail', price: 50.00, image: images.dessert, category: 'Alcohols' },
  { id: '11', name: 'Syrup Drink', description: 'Refreshing syrup drink', price: 30.00, image: images.drink, category: 'Beverages' },
  { id: '12', name: 'Virgin Mojito', description: 'Non-alcoholic mojito', price: 35.00, image: images.rice, category: 'Beverages' },
  { id: '13', name: 'Strawberry Milkshake', description: 'Fresh strawberry milkshake', price: 40.00, image: images.chicken, category: 'Beverages' },
  { id: '14', name: 'Coke', description: 'Cold Coca Cola', price: 20.00, image: images.seafood, category: 'Beverages' },
  { id: '15', name: 'Special Sauce', description: 'Chef special sauce combo', price: 45.00, image: images.extra1, category: 'Mains' },
  { id: '16', name: 'Fruit Salad', description: 'Premium mixed fruit salad', price: 85.00, image: images.extra2, category: 'Starters' },
  { id: '17', name: 'Green Salad', description: 'Fresh green salad', price: 35.00, image: images.extra3, category: 'Starters' },
  { id: '18', name: 'Fries', description: 'Crispy golden fries', price: 30.00, image: images.extra4, category: 'Starters' }
];

const CATEGORIES = ['All', 'Burgers', 'Mains', 'Starters', 'Desserts', 'Beverages', 'Alcohols'];

export const MenuScreen: React.FC<{ navigation: any }> = ({ navigation }) => {
  const { addToCart } = useCart();
  const { user } = useAuth();
  const [selectedCategory, setSelectedCategory] = useState('All');

  const handleAddToCart = (item: MenuItem) => {
    try {
      addToCart(item, user);
    } catch (error) {
      Alert.alert('Registration Required', 'Please register to add items to cart');
    }
  };

  const filteredItems = selectedCategory === 'All' 
    ? MENU_ITEMS 
    : MENU_ITEMS.filter(item => item.category === selectedCategory);

  const renderMenuItem = ({ item }: { item: MenuItem }) => (
    <View style={styles.itemContainer}>
      <Image source={item.image} style={styles.image} />
      <Text style={styles.itemName}>{item.name}</Text>
      <Text style={styles.itemDescription}>{item.description}</Text>
      <Text style={styles.itemPrice}>R{item.price}</Text>
      <TouchableOpacity style={styles.viewButton} onPress={() => navigation.navigate('MenuItemDetail', { item })}>
        <Text style={styles.viewButtonText}>View Item</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.addButton} onPress={() => handleAddToCart(item)}>
        <Text style={styles.addButtonText}>Add to Cart</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.filterTitle}>Filter by Category:</Text>
      <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.categoryContainer}>
        {CATEGORIES.map(category => (
          <TouchableOpacity
            key={category}
            style={[styles.categoryButton, selectedCategory === category && styles.selectedCategory]}
            onPress={() => setSelectedCategory(category)}
          >
            <Text style={[styles.categoryText, selectedCategory === category && styles.selectedCategoryText]}>
              {category}
            </Text>
          </TouchableOpacity>
        ))}
      </ScrollView>
      <FlatList
        data={filteredItems}
        renderItem={renderMenuItem}
        keyExtractor={item => item.id}
        numColumns={2}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, padding: 10, backgroundColor: '#f5f5f5' },
  filterTitle: { fontSize: 20, fontWeight: 'bold', marginBottom: 15, color: '#000', textAlign: 'center' },
  categoryContainer: { marginBottom: 20, height: 60, paddingVertical: 5 },
  categoryButton: { backgroundColor: '#ffffff', paddingHorizontal: 25, paddingVertical: 12, borderRadius: 25, marginRight: 12, height: 50, elevation: 3, shadowColor: '#000', shadowOffset: { width: 0, height: 2 }, shadowOpacity: 0.1, shadowRadius: 4 },
  selectedCategory: { backgroundColor: '#007bff' },
  categoryText: { fontSize: 14, color: '#666' },
  selectedCategoryText: { color: 'white', fontWeight: 'bold' },
  itemContainer: { flex: 1, margin: 5, padding: 10, backgroundColor: 'white', borderRadius: 8, elevation: 2 },
  image: { width: '100%', height: 200, borderRadius: 8, resizeMode: 'cover' },
  itemName: { fontSize: 16, fontWeight: 'bold', marginTop: 5 },
  itemDescription: { fontSize: 12, color: '#666', marginTop: 2 },
  itemPrice: { fontSize: 14, fontWeight: 'bold', color: '#007bff', marginTop: 5 },
  viewButton: { backgroundColor: '#28a745', padding: 8, borderRadius: 5, marginTop: 10 },
  viewButtonText: { color: 'white', textAlign: 'center', fontSize: 12 },
  addButton: { backgroundColor: '#007bff', padding: 8, borderRadius: 5, marginTop: 5 },
  addButtonText: { color: 'white', textAlign: 'center', fontSize: 12 }
});
