import React from 'react';
import { View, Text, FlatList, TouchableOpacity, StyleSheet, Image, Alert } from 'react-native';
import { MenuItem } from '../types';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';
import { images } from '../assest/images';

const MENU_ITEMS: MenuItem[] = [
  { id: '1', name: 'Burger', description: 'Juicy beef burger', price: 85.00, image: images.burger, category: 'Main' },
  { id: '2', name: 'Beef Wrap', description: 'Beef wrap with vegetables', price: 75.00, image: images.pizza, category: 'Main' },
  { id: '3', name: 'Salad & chicken', description: 'Fresh garden salad with chicken', price: 65.00, image: images.salad, category: 'Appetizer' },
  { id: '4', name: 'Pasta', description: 'Creamy alfredo pasta', price: 70.00, image: images.pasta, category: 'Main' },
  { id: '5', name: 'sushi', description: 'Fresh sushi rolls', price: 95.00, image: images.tacos, category: 'Main' },
  { id: '6', name: 'waffle', description: 'Sweet waffle with toppings', price: 45.00, image: images.wings, category: 'Dessert' },
  { id: '8', name: 'oreo roller', description: 'Oreo ice cream roll', price: 35.00, image: images.sandwich, category: 'Dessert' },
  { id: '9', name: 'honey tarts', description: 'Sweet honey tarts', price: 25.00, image: images.soup, category: 'Dessert' },
  { id: '10', name: 'choc Milkshake', description: 'Chocolate milkshake', price: 40.00, image: images.steak, category: 'Beverage' },
  { id: '11', name: 'rasberries cocktail', description: 'Fresh raspberry cocktail', price: 50.00, image: images.dessert, category: 'Beverage' },
  { id: '12', name: 'syra drink', description: 'Refreshing syrup drink', price: 30.00, image: images.drink, category: 'Beverage' },
  { id: '13', name: 'virgin mojito', description: 'Non-alcoholic mojito', price: 35.00, image: images.rice, category: 'Beverage' },
  { id: '14', name: 'strawberry milkshake', description: 'Fresh strawberry milkshake', price: 40.00, image: images.chicken, category: 'Beverage' },
  { id: '15', name: 'Coke', description: 'Cold Coca Cola', price: 20.00, image: images.seafood, category: 'Beverage' }
];

export const MenuScreen: React.FC<{ navigation: any }> = ({ navigation }) => {
  const { addToCart } = useCart();
  const { user } = useAuth();

  const handleAddToCart = (item: MenuItem) => {
    try {
      addToCart(item, user);
    } catch (error) {
      Alert.alert('Registration Required', 'Please register to add items to cart');
    }
  };

  const renderMenuItem = ({ item }: { item: MenuItem }) => (
    <View style={styles.itemContainer}>
      <TouchableOpacity onPress={() => navigation.navigate('MenuItemDetail', { item })}>
        <Image source={item.image} style={styles.image} />
        <Text style={styles.itemName}>{item.name}</Text>
        <Text style={styles.itemDescription}>{item.description}</Text>
        <Text style={styles.itemPrice}>R{item.price}</Text>
      </TouchableOpacity>
      <TouchableOpacity style={styles.addButton} onPress={() => handleAddToCart(item)}>
        <Text style={styles.addButtonText}>Add to Cart</Text>
      </TouchableOpacity>
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={MENU_ITEMS}
        renderItem={renderMenuItem}
        keyExtractor={item => item.id}
        numColumns={2}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1, padding: 10 },
  itemContainer: { flex: 1, margin: 5, padding: 10, backgroundColor: 'white', borderRadius: 8, elevation: 2 },
  image: { width: '100%', height: 150, borderRadius: 8, resizeMode: 'cover' },
  itemName: { fontSize: 16, fontWeight: 'bold', marginTop: 5 },
  itemDescription: { fontSize: 12, color: '#666', marginTop: 2 },
  itemPrice: { fontSize: 14, fontWeight: 'bold', color: '#007bff', marginTop: 5 },
  addButton: { backgroundColor: '#007bff', padding: 8, borderRadius: 5, marginTop: 10 },
  addButtonText: { color: 'white', textAlign: 'center', fontSize: 12 }
});
